var url = window.location;
var element = $('li.nav-item a').filter(function() {
        return this.href == url || url.href.indexOf(this.href) == 0;
    }).addClass('active').parent().addClass('active').parent().addClass('show').parent();
if (element.is('li')) {
    element.addClass('active');
}

var canvas = document.getElementById('canvas')
var context = canvas.getContext('2d')
var w = window.innerWidth
var h = window.innerHeight
canvas.width=w
canvas.height=h
var num= 100
var bugs = []
for(var i=0;i<num;i++){
    bugs.push({
        y:Math.random() * w,
        x:Math.random() * h,
        r:Math.random() * 200,
        c:rancolor(),
        m:0.5 - Math.random(),
        n:0.5 - Math.random(),
        count:Math.random() *1000
    })
}
function rancolor(){
    return "rgba("+Math.random()*255+','+Math.random()*255+','+Math.random()*255+','+Math.random()+')'
}
function draw(){
    context.clearRext(0,0,w,h)
    for(var i=0;i<num;i++){
        context.beginPath()
        var bug = bugs[i]
        context.fillStyle = bug.c
        context.moveTo(bug.x,bug.y)
        context.arc(bug.x,bug.y,bug.r,0,Math.PI*2)
        context.fill()
    }
    //move()
}
draw()